from config.db import DbHelperdb = DbHelper()class UserDao:    def user_row_mapper(self, row):        if row:            print("user_row_mapper", row)            return {                "user_id": row[0],                "username": row[1],                "created_dt": row[2],                "updated_dt": row[3]            }    def get_user_by_id(self, id):        sql="""            SELECT                 user_id,                 username,                                created_dt,                 updated_dt            FROM                users            WHERE                 user_id = ?        """        user = db.fetch_one(sql, (id, ))        db.close()        return self.user_row_mapper(user)    def get_user_by_username(self, username):        sql="""            SELECT                 user_id,                 username,                                created_dt,                 updated_dt            FROM                users            WHERE                 username = ?        """        user = db.fetch_one(sql, (username, ))        db.close()        return self.user_row_mapper(user)    def create_user(self, username, password):        sql="""            INSERT INTO                users(  username,                         password)            VALUES(                ?,                ?                            )                    """        cur = db.execute_query(sql, (username, password))        db.commit()        return self.get_user_by_id(cur.lastrowid)